        string symbol;
        string name;
        string description;
        address factory;
        address multitoken;
        uint256 quantity;
    }
    struct Loot {
        uint256 id;
        string symbol;
        string name;
        string description;
        uint256 lootHash;
        uint256 lootbox;
        address minter;
        address factory;
        address multitoken;
        uint256 probability;
        uint256 quantity;
    }
    struct SwapMeet {
        address swapMeetAddress;
    }
    struct Offer {
        uint256 id;
        address swapMeet;
        address owner;
        address pool;
        uint256 gem;
        uint256 quantity;
        address[] pools;
        uint256[] gems;
        uint256[] quantities;
        uint256 listingFee;
        uint256 acceptFee;
        uint256 references;
        bool missingTokenPenalty;
    }

    event FactoryCreated(address factory);

    event PoolCreated(
        address factory,
        address multitoken,
        address poolAddress,
        string symbol,
        string name,
        string description,
        uint256 category,
