        address opener,
        LootboxFactory factory,
        Lootbox lootbox,
        Loot[] receivedLoot
    );

    function indexGem(GemPool memory gemPool, Gem memory gem)
        external
        returns (bool);

    function indexGems(GemPool[] memory gemPools, Gem[] memory gems) external;

    function indexBulkGems(GemPool[] memory gemPools, Gem[] memory gems)
        external;

    function getOwnedGems(
        address gemPool,
        address multitoken,
        address account,
        uint256 page,
        uint256 count
    ) external view returns (uint256[] memory gems, uint256 gemLen);

    function indexGemPool(
        address gemPool,
        address multitoken,
        uint256 page,
        uint256 count
    ) external returns (Gem[] memory gems);
}
