     */
    event NFTGemCreated(
        address account,
        address pool,
        uint256 claimHash,
        uint256 gemHash,
        uint256 quantity
    );

    function setMultiToken(address token) external;

    function setGovernor(address addr) external;

    function setFeeTracker(address addr) external;

